import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { aiTrainerService } from "./services/aiTrainer";
import { insertWorkoutSessionSchema, insertUserProfileSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      const profile = await storage.getUserProfile(userId);
      res.json({ ...user, profile });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Dashboard data
  app.get('/api/dashboard', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const [stats, achievements, leaderboard, activityFeed] = await Promise.all([
        storage.getUserStats(userId),
        storage.getUserAchievements(userId),
        storage.getLeaderboard(10),
        storage.getActivityFeed(userId, 10),
      ]);

      res.json({
        stats,
        achievements,
        leaderboard,
        activityFeed,
      });
    } catch (error) {
      console.error("Error fetching dashboard data:", error);
      res.status(500).json({ message: "Failed to fetch dashboard data" });
    }
  });

  // Workouts
  app.get('/api/workouts', isAuthenticated, async (req: any, res) => {
    try {
      const workouts = await storage.getWorkouts(20);
      res.json(workouts);
    } catch (error) {
      console.error("Error fetching workouts:", error);
      res.status(500).json({ message: "Failed to fetch workouts" });
    }
  });

  app.get('/api/workouts/:id', isAuthenticated, async (req: any, res) => {
    try {
      const workout = await storage.getWorkoutById(req.params.id);
      if (!workout) {
        return res.status(404).json({ message: "Workout not found" });
      }
      res.json(workout);
    } catch (error) {
      console.error("Error fetching workout:", error);
      res.status(500).json({ message: "Failed to fetch workout" });
    }
  });

  // AI-generated workouts
  app.post('/api/ai/generate-workout', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getUserProfile(userId);
      const stats = await storage.getUserStats(userId);

      const workout = await aiTrainerService.generatePersonalizedWorkout({
        userProfile: profile,
        userStats: stats,
        preferences: req.body.preferences || {},
      });

      res.json(workout);
    } catch (error) {
      console.error("Error generating AI workout:", error);
      res.status(500).json({ message: "Failed to generate AI workout" });
    }
  });

  app.post('/api/ai/workout-insights', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const sessions = await storage.getUserWorkoutSessions(userId, 5);
      const profile = await storage.getUserProfile(userId);

      const insights = await aiTrainerService.generateInsights({
        recentSessions: sessions,
        userProfile: profile,
      });

      res.json(insights);
    } catch (error) {
      console.error("Error generating workout insights:", error);
      res.status(500).json({ message: "Failed to generate workout insights" });
    }
  });

  // Workout sessions
  app.post('/api/sessions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const sessionData = insertWorkoutSessionSchema.parse({
        ...req.body,
        userId,
      });

      const session = await storage.createWorkoutSession(sessionData);
      res.json(session);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid session data", errors: error.errors });
      }
      console.error("Error creating session:", error);
      res.status(500).json({ message: "Failed to create session" });
    }
  });

  app.patch('/api/sessions/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const sessionId = req.params.id;

      // Verify session belongs to user
      const sessions = await storage.getUserWorkoutSessions(userId);
      const session = sessions.find(s => s.id === sessionId);
      if (!session) {
        return res.status(404).json({ message: "Session not found" });
      }

      const updatedSession = await storage.updateWorkoutSession(sessionId, req.body);

      // Update user stats if session was completed
      if (req.body.status === 'completed' && req.body.completedAt) {
        const profile = await storage.getUserProfile(userId);
        if (profile) {
          await storage.updateUserProfile(userId, {
            totalWorkouts: (profile.totalWorkouts || 0) + 1,
            currentStreak: (profile.currentStreak || 0) + 1,
            totalPoints: (profile.totalPoints || 0) + 50, // Base points for completion
          });

          // Create activity
          await storage.createActivity(userId, {
            activityType: 'workout_completed',
            title: 'Completed workout',
            description: `Finished a ${updatedSession.totalDuration || 0} minute workout`,
            points: 50,
          });
        }
      }

      res.json(updatedSession);
    } catch (error) {
      console.error("Error updating session:", error);
      res.status(500).json({ message: "Failed to update session" });
    }
  });

  // User profile
  app.get('/api/profile', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getUserProfile(userId);
      res.json(profile);
    } catch (error) {
      console.error("Error fetching profile:", error);
      res.status(500).json({ message: "Failed to fetch profile" });
    }
  });

  app.patch('/api/profile', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profileData = insertUserProfileSchema.partial().parse(req.body);

      const updatedProfile = await storage.updateUserProfile(userId, profileData);
      res.json(updatedProfile);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid profile data", errors: error.errors });
      }
      console.error("Error updating profile:", error);
      res.status(500).json({ message: "Failed to update profile" });
    }
  });

  // Workout inbox routes
  app.get('/api/workout-inbox', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      let inboxItems = await storage.getWorkoutInboxItems(userId);

      // If no items exist, create some sample data for demonstration
      if (inboxItems.length === 0) {
        const sampleItems = [
          {
            userId,
            workoutData: {
              heartRateData: [78, 142, 165, 158, 171, 159, 88],
              steps: 3420,
              duration: 52
            },
            autoDetectedType: 'Basketball',
            confidence: '0.91',
            title: 'Basketball Training - Apple Watch',
            duration: 52,
            caloriesBurned: 486,
            averageHeartRate: 159,
            aiSummary: 'High-intensity interval pattern with quick direction changes. Typical basketball training session.',
          },
          {
            userId,
            workoutData: {
              heartRateData: [85, 95, 98, 92, 89],
              steps: 890,
              duration: 25
            },
            autoDetectedType: 'Strength Training',
            confidence: '0.87',
            title: 'Gym Session - Garmin',
            duration: 25,
            caloriesBurned: 198,
            averageHeartRate: 92,
            aiSummary: 'Low step count with sustained moderate heart rate. Resistance training detected.',
          },
          {
            userId,
            workoutData: {
              heartRateData: [92, 156, 168, 175, 162, 148, 95],
              steps: 4200,
              duration: 38
            },
            autoDetectedType: 'Cardio',
            confidence: '0.85',
            title: 'Morning Run - Apple Watch',
            duration: 38,
            caloriesBurned: 342,
            averageHeartRate: 157,
            aiSummary: 'Steady elevated heart rate with consistent step pattern. Running workout identified.',
          }
        ];

        // Create sample items
        for (const item of sampleItems) {
          await storage.createWorkoutInboxItem(item);
        }

        // Fetch the newly created items
        inboxItems = await storage.getWorkoutInboxItems(userId);
      }

      res.json(inboxItems);
    } catch (error) {
      console.error("Error fetching workout inbox:", error);
      res.status(500).json({ message: "Failed to fetch workout inbox" });
    }
  });

  app.post('/api/workout-inbox/:id/categorize', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const itemId = req.params.id;
      const { category } = req.body;

      const updatedItem = await storage.categorizeWorkoutInboxItem(itemId, userId, category);
      res.json(updatedItem);
    } catch (error) {
      console.error("Error categorizing workout:", error);
      res.status(500).json({ message: "Failed to categorize workout" });
    }
  });

  app.post('/api/workout-inbox/:id/ignore', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const itemId = req.params.id;

      const updatedItem = await storage.ignoreWorkoutInboxItem(itemId, userId);
      res.json(updatedItem);
    } catch (error) {
      console.error("Error ignoring workout:", error);
      res.status(500).json({ message: "Failed to ignore workout" });
    }
  });

  // AI workout message processing
  app.post('/api/ai/process-workout-message', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { message } = req.body;

      // Simple pattern matching for workout detection
      const workoutPatterns = {
        basketball: /basketball|ball|court|dribbl|shoot/i,
        cardio: /cardio|run|jog|sprint/i,
        strength: /strength|weight|lift|gym/i,
      };

      // Extract duration from message
      const durationMatch = message.match(/(\d+)\s*(min|minute|hour)/i);
      const duration = durationMatch ? parseInt(durationMatch[1]) : 30;

      let workoutType = 'general';
      let category = 'other';

      if (workoutPatterns.basketball.test(message)) {
        workoutType = 'basketball training';
        category = 'basketball_training';
      } else if (workoutPatterns.cardio.test(message)) {
        workoutType = 'cardio session';
        category = 'cardio';
      } else if (workoutPatterns.strength.test(message)) {
        workoutType = 'strength training';
        category = 'strength';
      }

      // Create a workout session entry (for manual/AI entries, we'll use a default workoutId)
      await storage.createWorkoutSession({
        userId,
        workoutId: 'manual-entry', // placeholder for manual entries
        status: 'completed',
        totalDuration: duration * 60, // convert to seconds
        caloriesBurned: Math.floor(duration * 8), // rough estimate
        notes: `AI logged: ${message}`,
      });

      const response = `Great! I've logged your ${duration}-minute ${workoutType}. Keep up the excellent work!`;

      res.json({ 
        response,
        workoutCreated: true,
        category,
        duration
      });
    } catch (error) {
      console.error("Error processing workout message:", error);
      res.status(500).json({ message: "Failed to process workout message" });
    }
  });

  // Mock biometric data endpoint (for wearable integration demo)
  app.get('/api/biometrics/live', isAuthenticated, async (req: any, res) => {
    // Mock real-time biometric data
    const mockData = {
      heartRate: Math.floor(Math.random() * (180 - 60) + 60),
      steps: Math.floor(Math.random() * 5000 + 2000),
      calories: Math.floor(Math.random() * 500 + 200),
      hrv: Math.floor(Math.random() * 50 + 30),
      timestamp: new Date().toISOString(),
    };

    res.json(mockData);
  });

  // AI fitness plan generation
  app.post('/api/ai/generate-fitness-plan', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const profile = await storage.getUserProfile(userId);
      const stats = await storage.getUserStats(userId);

      const generatedPlan = await aiTrainerService.generateFitnessPlan({
        userProfile: profile,
        userStats: stats,
        preferences: req.body.preferences || {},
      });

      // Map methodology to valid planType enum values
      const methodologyToPlanType = (methodology: string) => {
        const methodologyLower = methodology.toLowerCase();
        switch (methodologyLower) {
          case 'goata':
            return 'mixed';
          case 'soviet':
            return 'strength';
          case 'nba':
            return 'basketball';
          case 'hypertrophy':
            return 'strength';
          default:
            return 'mixed';
        }
      };

      // Save the generated plan to the database
      const planToSave = {
        name: generatedPlan.name,
        description: generatedPlan.description,
        methodology: generatedPlan.methodology,
        planType: methodologyToPlanType(generatedPlan.methodology || 'mixed'),
        difficulty: generatedPlan.difficulty,
        duration: generatedPlan.duration,
        workoutsPerWeek: generatedPlan.workoutsPerWeek,
        aiGenerated: true,
        isPopular: false,
        createdBy: userId,
      };

      const savedPlan = await storage.createFitnessPlan(planToSave);

      // Return the saved plan with its database ID
      res.json({
        ...generatedPlan,
        id: savedPlan.id,
        createdBy: savedPlan.createdBy,
        createdAt: savedPlan.createdAt,
      });
    } catch (error) {
      console.error("Error generating AI fitness plan:", error);
      res.status(500).json({ message: "Failed to generate AI fitness plan" });
    }
  });

  // Fitness Plans CRUD
  app.get('/api/fitness-plans', isAuthenticated, async (req, res) => {
    try {
      const plans = await storage.getFitnessPlans(20);
      res.json(plans);
    } catch (error) {
      console.error("Error fetching fitness plans:", error);
      res.status(500).json({ message: "Failed to fetch fitness plans" });
    }
  });

  app.get('/api/fitness-plans/:id', isAuthenticated, async (req, res) => {
    try {
      const plan = await storage.getFitnessPlanWithWorkouts(req.params.id);
      if (!plan) {
        return res.status(404).json({ message: "Fitness plan not found" });
      }
      res.json(plan);
    } catch (error) {
      console.error("Error fetching fitness plan:", error);
      res.status(500).json({ message: "Failed to fetch fitness plan" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  userProfiles,
  fitnessPlans,
  planWorkouts,
  workouts,
  workoutSessions,
  exercises,
  achievements,
  userAchievements,
  biometricData,
  activityFeed,
  workoutInbox,
  type User,
  type UpsertUser,
  type FitnessPlan,
  type InsertFitnessPlan,
  type PlanWorkout,
  type InsertPlanWorkout,
  type Workout,
  type WorkoutSession,
  type UserProfile,
  type Achievement,
  type BiometricData,
  type WorkoutInbox,
  type Exercise,
  type InsertExercise,
  type InsertWorkout,
  type InsertWorkoutSession,
  type InsertUserProfile,
  type InsertWorkoutInbox,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, sql, count } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // User profile operations
  getUserProfile(userId: string): Promise<UserProfile | undefined>;
  updateUserProfile(userId: string, data: Partial<InsertUserProfile>): Promise<UserProfile>;

  // Fitness Plan operations (top level of hierarchy)
  getFitnessPlans(limit?: number): Promise<FitnessPlan[]>;
  getFitnessPlanById(id: string): Promise<FitnessPlan | undefined>;
  getFitnessPlanWithWorkouts(id: string): Promise<any>;
  createFitnessPlan(plan: InsertFitnessPlan): Promise<FitnessPlan>;
  addWorkoutToPlan(planWorkout: InsertPlanWorkout): Promise<PlanWorkout>;

  // Workout operations (middle level of hierarchy)
  getWorkouts(limit?: number): Promise<Workout[]>;
  getWorkoutById(id: string): Promise<Workout | undefined>;
  getWorkoutWithExercises(id: string): Promise<any>;
  createWorkout(workout: InsertWorkout): Promise<Workout>;
  addExerciseToWorkout(exercise: InsertExercise): Promise<Exercise>;

  // Session operations
  createWorkoutSession(session: InsertWorkoutSession): Promise<WorkoutSession>;
  updateWorkoutSession(sessionId: string, data: Partial<WorkoutSession>): Promise<WorkoutSession>;
  getUserWorkoutSessions(userId: string, limit?: number): Promise<WorkoutSession[]>;

  // Achievement operations
  getUserAchievements(userId: string): Promise<Achievement[]>;
  unlockAchievement(userId: string, achievementId: string): Promise<void>;

  // Analytics operations
  getUserStats(userId: string): Promise<{
    totalWorkouts: number;
    currentStreak: number;
    totalPoints: number;
    averageHeartRate: number;
    recoveryScore: number;
  }>;

  // Leaderboard operations
  getLeaderboard(limit?: number): Promise<Array<{
    user: User;
    profile: UserProfile;
    rank: number;
  }>>;

  // Activity feed
  getActivityFeed(userId: string, limit?: number): Promise<any[]>;
  createActivity(userId: string, activity: {
    activityType: string;
    title: string;
    description?: string;
    points?: number;
    metadata?: any;
  }): Promise<void>;

  // Workout inbox operations
  getWorkoutInboxItems(userId: string): Promise<WorkoutInbox[]>;
  createWorkoutInboxItem(item: InsertWorkoutInbox): Promise<WorkoutInbox>;
  categorizeWorkoutInboxItem(itemId: string, userId: string, category: string): Promise<WorkoutInbox>;
  ignoreWorkoutInboxItem(itemId: string, userId: string): Promise<WorkoutInbox>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();

    // Create user profile if it doesn't exist
    await db
      .insert(userProfiles)
      .values({
        userId: user.id,
        experience: 'beginner',
        totalWorkouts: 0,
        totalPoints: 0,
        currentStreak: 0,
        longestStreak: 0,
        skillLevel: 1,
        recoveryScore: sql`75.0`,
      })
      .onConflictDoNothing();

    return user;
  }

  async getUserProfile(userId: string): Promise<UserProfile | undefined> {
    const [profile] = await db.select().from(userProfiles).where(eq(userProfiles.userId, userId));
    return profile;
  }

  async updateUserProfile(userId: string, data: Partial<InsertUserProfile>): Promise<UserProfile> {
    const [profile] = await db
      .update(userProfiles)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(userProfiles.userId, userId))
      .returning();
    return profile;
  }

  async getWorkouts(limit = 20): Promise<Workout[]> {
    return await db
      .select()
      .from(workouts)
      .orderBy(desc(workouts.createdAt))
      .limit(limit);
  }

  async getWorkoutById(id: string): Promise<Workout | undefined> {
    const [workout] = await db.select().from(workouts).where(eq(workouts.id, id));
    return workout;
  }

  // Fitness Plan operations (top level of hierarchy)
  async getFitnessPlans(limit = 20): Promise<FitnessPlan[]> {
    return await db
      .select()
      .from(fitnessPlans)
      .orderBy(desc(fitnessPlans.createdAt))
      .limit(limit);
  }

  async getFitnessPlanById(id: string): Promise<FitnessPlan | undefined> {
    const [plan] = await db.select().from(fitnessPlans).where(eq(fitnessPlans.id, id));
    return plan;
  }

  async getFitnessPlanWithWorkouts(id: string): Promise<any> {
    const plan = await db.query.fitnessPlans.findFirst({
      where: eq(fitnessPlans.id, id),
      with: {
        planWorkouts: {
          with: {
            workout: {
              with: {
                exercises: true,
              }
            }
          },
          orderBy: [planWorkouts.week, planWorkouts.day, planWorkouts.order],
        }
      }
    });
    return plan;
  }

  async createFitnessPlan(plan: InsertFitnessPlan): Promise<FitnessPlan> {
    const [newPlan] = await db.insert(fitnessPlans).values(plan).returning();
    return newPlan;
  }

  async addWorkoutToPlan(planWorkout: InsertPlanWorkout): Promise<PlanWorkout> {
    const [newPlanWorkout] = await db.insert(planWorkouts).values(planWorkout).returning();
    return newPlanWorkout;
  }

  async createWorkout(workout: InsertWorkout): Promise<Workout> {
    const [newWorkout] = await db.insert(workouts).values(workout).returning();
    return newWorkout;
  }

  async getWorkoutWithExercises(id: string): Promise<any> {
    const workout = await db.query.workouts.findFirst({
      where: eq(workouts.id, id),
      with: {
        exercises: {
          orderBy: exercises.order,
        }
      }
    });
    return workout;
  }

  async addExerciseToWorkout(exercise: InsertExercise): Promise<Exercise> {
    const [newExercise] = await db.insert(exercises).values(exercise).returning();
    return newExercise;
  }

  async createWorkoutSession(session: InsertWorkoutSession): Promise<WorkoutSession> {
    const [newSession] = await db.insert(workoutSessions).values(session).returning();
    return newSession;
  }

  async updateWorkoutSession(sessionId: string, data: Partial<WorkoutSession>): Promise<WorkoutSession> {
    const [session] = await db
      .update(workoutSessions)
      .set(data)
      .where(eq(workoutSessions.id, sessionId))
      .returning();
    return session;
  }

  async getUserWorkoutSessions(userId: string, limit = 10): Promise<WorkoutSession[]> {
    return await db
      .select()
      .from(workoutSessions)
      .where(eq(workoutSessions.userId, userId))
      .orderBy(desc(workoutSessions.startedAt))
      .limit(limit);
  }

  async getUserAchievements(userId: string): Promise<Achievement[]> {
    const result = await db
      .select({
        achievement: achievements,
      })
      .from(userAchievements)
      .innerJoin(achievements, eq(userAchievements.achievementId, achievements.id))
      .where(eq(userAchievements.userId, userId));

    return result.map(r => r.achievement);
  }

  async unlockAchievement(userId: string, achievementId: string): Promise<void> {
    await db.insert(userAchievements).values({
      userId,
      achievementId,
    }).onConflictDoNothing();
  }

  async getUserStats(userId: string): Promise<{
    totalWorkouts: number;
    currentStreak: number;
    totalPoints: number;
    averageHeartRate: number;
    recoveryScore: number;
  }> {
    const [profile] = await db.select().from(userProfiles).where(eq(userProfiles.userId, userId));

    // Get average heart rate from recent sessions
    const [heartRateResult] = await db
      .select({
        avgHeartRate: sql<number>`AVG(${workoutSessions.averageHeartRate})`,
      })
      .from(workoutSessions)
      .where(and(
        eq(workoutSessions.userId, userId),
        sql`${workoutSessions.averageHeartRate} IS NOT NULL`
      ));

    return {
      totalWorkouts: profile?.totalWorkouts || 0,
      currentStreak: profile?.currentStreak || 0,
      totalPoints: profile?.totalPoints || 0,
      averageHeartRate: Math.round(heartRateResult?.avgHeartRate || 0),
      recoveryScore: parseFloat(profile?.recoveryScore?.toString() || '0'),
    };
  }

  async getLeaderboard(limit = 10): Promise<Array<{
    user: User;
    profile: UserProfile;
    rank: number;
  }>> {
    const results = await db
      .select({
        user: users,
        profile: userProfiles,
      })
      .from(userProfiles)
      .innerJoin(users, eq(userProfiles.userId, users.id))
      .orderBy(desc(userProfiles.totalPoints))
      .limit(limit);

    return results.map((result, index) => ({
      user: result.user,
      profile: result.profile,
      rank: index + 1,
    }));
  }

  async getActivityFeed(userId: string, limit = 20): Promise<any[]> {
    // Get activities from user and their friends
    return await db
      .select({
        id: activityFeed.id,
        user: users,
        activityType: activityFeed.activityType,
        title: activityFeed.title,
        description: activityFeed.description,
        points: activityFeed.points,
        createdAt: activityFeed.createdAt,
        metadata: activityFeed.metadata,
      })
      .from(activityFeed)
      .innerJoin(users, eq(activityFeed.userId, users.id))
      .where(eq(activityFeed.isPublic, true))
      .orderBy(desc(activityFeed.createdAt))
      .limit(limit);
  }

  async createActivity(userId: string, activity: {
    activityType: string;
    title: string;
    description?: string;
    points?: number;
    metadata?: any;
  }): Promise<void> {
    await db.insert(activityFeed).values({
      userId,
      ...activity,
    });
  }

  // Workout inbox operations
  async getWorkoutInboxItems(userId: string): Promise<WorkoutInbox[]> {
    const items = await db
      .select()
      .from(workoutInbox)
      .where(eq(workoutInbox.userId, userId))
      .orderBy(desc(workoutInbox.receivedAt));

    return items;
  }

  async createWorkoutInboxItem(item: InsertWorkoutInbox): Promise<WorkoutInbox> {
    const [created] = await db
      .insert(workoutInbox)
      .values(item)
      .returning();

    return created;
  }

  async categorizeWorkoutInboxItem(itemId: string, userId: string, category: string): Promise<WorkoutInbox> {
    const [updated] = await db
      .update(workoutInbox)
      .set({
        status: 'categorized',
        category,
        processedAt: new Date(),
      })
      .where(and(eq(workoutInbox.id, itemId), eq(workoutInbox.userId, userId)))
      .returning();

    return updated;
  }

  async ignoreWorkoutInboxItem(itemId: string, userId: string): Promise<WorkoutInbox> {
    const [updated] = await db
      .update(workoutInbox)
      .set({
        status: 'ignored',
        processedAt: new Date(),
      })
      .where(and(eq(workoutInbox.id, itemId), eq(workoutInbox.userId, userId)))
      .returning();

    return updated;
  }
}

export const storage = new DatabaseStorage();

